from django.shortcuts import render
from django.http import HttpResponse
from rest_framework.response import Response
from rest_framework.views import APIView
from rest_framework import status
import pandas as pd

from drf_yasg import openapi
from drf_yasg.utils import swagger_auto_schema

from destination.models import Package, Destination, DestinationGalleryImages, DestinationReview
from booking.models import DestinationBook
from activities.models import Activity
from collection.models import Collection
from departure.models import Departure
from review.models import Review

from destination.serializers.destination_review_serializers import DestinationReviewWriteSerializers
from destination.serializers.destination_serializers import DestinationWriteSerializers
from destination.serializers.destinationgalleryimages_serializers import DestinationGalleryImagesWriteSerializers
from destination.serializers.package_serializers import PackageWriteSerializers
from review.serializers.review_serializers import ReviewWriteSerializers
from booking.serializers.destinationbook_serializers import DestinationBookWriteSerializers
from activities.serializers.activity_serializers import ActivityWriteSerializers
from collection.serializers.collection_serializers import CollectionWriteSerializers
from departure.serializers.departure_serializers import DepartureWriteSerializers

# Create your views here.

class ImportExcel(APIView):
    @swagger_auto_schema(
        request_body=openapi.Schema(
            type=openapi.TYPE_OBJECT,
            properties={
                'excel_file': openapi.Schema(type=openapi.TYPE_FILE),
            },
            required=['excel_file']
        ),
        operation_summary="Upload Excel file",
        operation_description="Upload Excel file and import data",
    )
    def post(self, request, type, format=None):
        file = request.FILES.get('excel_file')
        if not file:
            return Response({"error": "No file provided"}, status=status.HTTP_400_BAD_REQUEST)

        df = pd.read_csv(file)
        datas = df.to_dict(orient='records')

        if type == "package":
            create_update_with_fk(Package, PackageWriteSerializers, datas, 'name')
        elif type == "destination":
            create_update_with_fk(Destination, DestinationWriteSerializers, datas, 'name')
        elif type == "destination-gallery-images":
            create_update_with_fk(DestinationGalleryImages, DestinationGalleryImagesWriteSerializers, datas, 'image')
        elif type == "destination-review":
            create_update_with_fk(DestinationReview, DestinationReviewWriteSerializers, datas, 'review')
        elif type == "destination-book":
            create_update_with_fk(DestinationBook, DestinationBookWriteSerializers, datas, 'booking_reference')
        elif type == "activity":
            create_update_with_fk(Activity, ActivityWriteSerializers, datas, 'name')
        elif type == "collection":
            create_update_with_fk(Collection, CollectionWriteSerializers, datas, 'name')
        elif type == "departure":
            create_update_with_fk(Departure, DepartureWriteSerializers, datas, 'departure_date')
        elif type == "review":
            create_update_with_fk(Review, ReviewWriteSerializers, datas, 'title')
        else:
            return Response({"message": "Unknown file type"}, status=status.HTTP_400_BAD_REQUEST)

        return Response({"message": "File processed successfully"}, status=status.HTTP_201_CREATED)

def create_update_with_fk(my_model, my_serializer, datas, unique_field_name):
    for record in datas:
        # Process foreign keys by looking up related models by name
        for field_name, field_value in record.items():
            if isinstance(field_value, str):
                try:
                    related_model = my_model._meta.get_field(field_name).related_model
                    related_instance = related_model.objects.filter(name=field_value).first()
                    if related_instance:
                        record[field_name] = related_instance.id
                    else:
                        print(f"Related instance for {field_name} with value '{field_value}' not found.")
                except AttributeError:
                    # Skip if the field is not a foreign key
                    continue

        # Handle the creation or update of the main record
        existing_data = my_model.objects.filter(**{unique_field_name: record[unique_field_name]})
        if existing_data.exists():
            existing_data = existing_data.first()
            serializer = my_serializer(existing_data, data=record)
            if serializer.is_valid():
                serializer.save()
            else:
                print(serializer.errors)
        else:
            serializer = my_serializer(data=record)
            if serializer.is_valid():
                serializer.save()
            else:
                print(serializer.errors)
